using System;
using ALICE_Core;
using System.Collections.Generic;
using ALICE_Internal;
using ALICE_Objects;
using ALICE_Settings;
using ALICE.Properties;
using ALICE_Equipment;
using ALICE_Debug;
using ALICE_Data;

namespace ALICE_Events
{
    /// <summary>
    /// Object Data Class
    /// </summary>
    public class Loadout : Base
    {
        public string Ship { get; set; }
        public decimal ShipID { get; set; }
        public string ShipName { get; set; }
        public string ShipIdent { get; set; }
        public decimal HullValue { get; set; }
        public decimal HullHealth { get; set; }
        public decimal ModulesValue { get; set; }
        public decimal Rebuy { get; set; }
        public List<Module> Modules { get; set; }

        //Default Constructor
        public Loadout()
        {
            Ship = Str();
            ShipID = Dec();
            ShipName = Str();
            ShipIdent = Str();
            HullValue = Dec();
            HullHealth = Dec();
            ModulesValue = Dec();
            Rebuy = Dec();
            Modules = new List<Module>();
        }

        public class Module : Catch
        {
            //Always Used
            public string Slot { get; set; }
            public string Item { get; set; }
            public bool On { get; set; }
            public decimal Priority { get; set; }
            public decimal Health { get; set; }
            public decimal Value { get; set; }

            //Sometimes Used
            public decimal AmmoInHopper { get; set; }
            public decimal AmmoInClip { get; set; }
            public EngineeringInfo Engineering { get; set; }

            public Module()
            {
                Slot = Str();
                Item = Str();
                On = Default.True;
                Priority = Dec();
                Health = Dec();
                Value = Dec();
                AmmoInHopper = Dec();
                AmmoInClip = Dec();
                Engineering = new EngineeringInfo();
            }
        }

        public class EngineeringInfo : Catch
        {
            public string Engineer { get; set; }
            public decimal EngineerID { get; set; }
            public decimal BlueprintID { get; set; }
            public string BlueprintName { get; set; }
            public decimal Level { get; set; }
            public decimal Quality { get; set; }
            public string ExperimentalEffect { get; set; }
            public string ExperimentalEffect_Localised { get; set; }
            public List<Modifer> Modifiers { get; set; }

            public EngineeringInfo()
            {
                Engineer = Str();
                EngineerID = Dec();
                BlueprintID = Dec();
                BlueprintName = Str();
                Level = Dec();
                Quality = Dec();
                ExperimentalEffect = Str();
                ExperimentalEffect_Localised = Str();
                Modifiers = new List<Modifer>();
            }
        }

        public class Modifer : Catch
        {
            public string Label { get; set; }
            public decimal Value { get; set; }
            public decimal OriginalValue { get; set; }
            public decimal LessIsGood { get; set; }

            public Modifer()
            {
                Label = Str();
                Value = Dec();
                OriginalValue = Dec();
                LessIsGood = Dec();
            }
        }
    }

    /// <summary>
    /// Event Logic & Data Storage Class
    /// </summary>
    public class Event_Loadout : Event
    {
        //Event Instance
        public Loadout I { get; set; } = new Loadout();

        //Variable Generation
        public override void Generate(object O)
        {
            try
            {
                Variables.Record(Name + "_Name", I.ShipName);
                Variables.Record(Name + "_CallSign", I.ShipIdent);
                Variables.Record(Name + "_HullValue", I.HullValue);
                Variables.Record(Name + "_HullHealth", I.HullHealth);
                Variables.Record(Name + "_ModuleValue", I.ModulesValue);
                Variables.Record(Name + "_TotalValue", I.HullValue + I.ModulesValue);
                Variables.Record(Name + "_Rebuy", I.Rebuy);
                Variables.Record(Name + "_Type", I.Ship);
                Variables.Record(Name + "_ID", I.ShipID);

                foreach (var M in I.Modules)
                {
                    string Slot = GetSlot(M.Slot, false);
                    Variables.Record(Slot + "_Item", M.Item);
                    Variables.Record(Slot + "_On", M.On);
                    Variables.Record(Slot + "_Priority", M.Priority);
                    Variables.Record(Slot + "_Health", M.Health);
                    Variables.Record(Slot + "_Value", M.Value);
                }
            }
            catch (Exception ex)
            {
                ExceptionGenerate(Name, ex);
            }
        }

        //Plugin Logic Preparations
        public override void Prepare(object O)
        {
            try
            {
                //Update Event Instance
                I = (Loadout)O;
            }
            catch (Exception ex)
            {
                ExceptionPrepare(Name, ex);
            }
        }

        //Plugin Logic Process
        public override void Process(object O)
        {
            try
            {
                //Clear Current Module Data
                IData.ShipModules.Clear();

                //Load Mothership Data
                switch (IVehicles.Vehicle)
                {
                    case IVehicles.V.Mothership:

                        //Validate Ship ID
                        if (IObjects.Mothership.I.ID != -1                  //Default Value For Blank Object (Fresh Ship)
                         && IObjects.Mothership.I.ID != I.ShipID)       //Ship ID Should Match, Otherwise Its A Different Ship.
                        {
                            //Event Logger
                            if (ICheck.Initialized(ClassName))
                            {
                                Logger.Event("Updating New Mothership Data.");
                            }

                            //Different Ship, Reset Object
                            IObjects.Mothership.New(I.Event);

                            //FingerPrint Generation
                            string FP = I.ShipID + " " + I.Ship + " (" + ISettings.Commander + ")";

                            //Load Ship Data, If It Exist
                            IObjects.Mothership = new Object_Mothership().Load(ClassName, FP);
                        }
                        //Same Ship, Reset Equipment.
                        else
                        {
                            IObjects.Mothership.E = new Object_Mothership.Equipment();
                        }

                        break;

                    default:

                        if (ICheck.Initialized(ClassName))
                        { Logger.Log(ClassName, "Mothership Loadout Is Not Available, Loading Best Guess... Hope This Works.", Logger.Yellow); }

                        //Load Saved Mothership Data.                    
                        IObjects.Mothership = new Object_Mothership().Load(ClassName, null);

                        //Load Firegroup Settings
                        ISettings.Firegroup.Load();

                        break;
                }

                //Update Ship Object
                switch (IVehicles.Vehicle)
                {
                    case IVehicles.V.Mothership:

                        //Update Object Data
                        IObjects.Mothership.Update(I);

                        //Save Mothership Data.
                        new Object_Mothership().Save(IObjects.Mothership, ClassName);

                        break;

                    case IVehicles.V.Fighter:
                        //No Logic
                        break;

                    case IVehicles.V.SRV:
                        //No Logic
                        break;

                    default:
                        break;
                }

                //Validate NPC Crew Member
                if (IVehicles.Installed(IEquipment.E.Fighter_Hangar) == false)
                {
                    IStatus.NPC_Crew = false;
                    Miscellanous.Default["NPC_Crew"] = IStatus.NPC_Crew;
                    Miscellanous.Default.Save();
                }

                //Update Equipment Settings.           
                IVehicles.PullEquipmentSettings();

                //Log Mothership Load
                Logger.Log(ClassName, "Loaded " + IObjects.Mothership.I.FingerPrint, Logger.Purple);

                //Load Firegroup Settings
                ISettings.Firegroup.Load();
            }
            catch (Exception ex)
            {
                ExceptionProcess(Name, ex);
            }
        }
    }
}